import React, { useEffect, useState } from 'react';

const ResultProcessor = ({ weights, answers }) => {
    const [displayMetrics, setDisplayMetrics] = useState('');
    const [complete, setComplete] = useState(false);

    useEffect(() => {
        // Generate the JSON result
        const resultData = {
            STATUS: 'ANALYSIS_COMPLETE',
            TIMESTAMP: new Date().toISOString(),
            CORE_VECTORS: weights,
            DOMINANT_TRAIT: weights.x > 0 ? 'INTUITIVE_LEAP_ENGINE' : 'LOGICAL_STRUCTURE_CORE',
            NOTE: 'Subject demonstrates high capacity for future adaptation.'
        };

        const text = JSON.stringify(resultData, null, 2);
        let i = 0;

        const interval = setInterval(() => {
            setDisplayMetrics(text.substring(0, i));
            i++;
            if (i > text.length) {
                clearInterval(interval);
                setComplete(true);
            }
        }, 10); // Fast typing

        return () => clearInterval(interval);
    }, [weights]);

    return (
        <div className="w-full max-w-2xl font-mono p-6 border border-cyan-500/30 bg-black/50 backdrop-blur">
            <h3 className="text-xl text-green-400 mb-4 animate-pulse">
                {complete ? '>> DIAGNOSIS COMPLETE' : '>> PROCESSING...'}
            </h3>
            <pre className="text-xs md:text-sm text-cyan-300 leading-tight whitespace-pre-wrap">
                {displayMetrics}
                <span className="inline-block w-2 h-4 bg-cyan-400 ml-1 animate-pulse"></span>
            </pre>
        </div>
    );
};

export default ResultProcessor;

import React, { useEffect, useState } from 'react';

const SNIPPETS = [
    `function encrypt(void) { return NULL; }`,
    `const MEMORY_LEAK = true;`,
    `if (hope > logic) { rewrite(); }`,
    `while(searching) { expand(); }`,
    `ReferenceError: PAST not defined`,
    `0x000F4A2C [SEGMENTATION FAULT]`,
    `>> UPLOADING CONSCIOUSNESS...`,
];

const MetadataLog = ({ weights }) => {
    const [logs, setLogs] = useState([]);

    useEffect(() => {
        // Add a log when weights change significantly or randomly
        if (Math.random() > 0.3) {
            setLogs(prev => [
                `Vector Updated: { x: ${weights?.x.toFixed(2)}, y: ${weights?.y.toFixed(2)} }`,
                ...prev
            ].slice(0, 15));
        }
    }, [weights]);

    useEffect(() => {
        const interval = setInterval(() => {
            if (Math.random() > 0.5) {
                setLogs(prev => [
                    SNIPPETS[Math.floor(Math.random() * SNIPPETS.length)],
                    ...prev
                ].slice(0, 15));
            }
        }, 1200);
        return () => clearInterval(interval);
    }, []);

    return (
        <div className="w-1/4 max-w-[300px] border-l border-cyan-900/30 p-4 z-10 flex flex-col backdrop-blur-sm bg-slate-950/80 hidden md:flex">
            <h2 className="text-xs font-bold border-b border-cyan-800 pb-2 mb-4 text-cyan-600">METADATA_LOG</h2>
            <div className="flex-1 overflow-hidden font-mono text-[10px] text-green-500/60 space-y-2 text-right">
                {logs.map((log, i) => (
                    <div key={i} className="border-r-2 border-green-900/50 pr-2">
                        {log}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MetadataLog;

import React, { useEffect, useState } from 'react';

const KERNEL_LINES = [
    'Initialising neural handshake...',
    'Scanning biometric signatures...',
    'Decrypting cognitive patterns...',
    'Accessing deep memory sectors...',
    'Filtering emotional noise...',
    'Calibrating logic gates...',
    'Optimizing synapse latency...',
    'Verifying soul integrity...',
];

const PersonalKernelSidebar = ({ phase }) => {
    const [lines, setLines] = useState([]);

    useEffect(() => {
        const interval = setInterval(() => {
            setLines(prev => {
                const newLine = KERNEL_LINES[Math.floor(Math.random() * KERNEL_LINES.length)];
                const timestamp = new Date().toLocaleTimeString('en-US', { hour12: false });
                return [`[${timestamp}] ${newLine}`, ...prev].slice(0, 20);
            });
        }, 800);
        return () => clearInterval(interval);
    }, []);

    return (
        <div className="w-1/4 max-w-[300px] border-r border-cyan-900/30 p-4 z-10 flex flex-col backdrop-blur-sm bg-slate-950/80 hidden md:flex">
            <h2 className="text-xs font-bold border-b border-cyan-800 pb-2 mb-4 text-cyan-600">
                PERSONAL_KERNEL :: {phase}
            </h2>
            <div className="flex-1 overflow-hidden font-mono text-[10px] text-cyan-700/70 font-thin space-y-1">
                {lines.map((line, i) => (
                    <div key={i} className="opacity-80 hover:opacity-100 hover:text-cyan-400 cursor-default transition-opacity">
                        {line}
                    </div>
                ))}
            </div>
            <div className="mt-4 border-t border-cyan-900/30 pt-2">
                <div className="text-[10px] text-cyan-900">CPU: {Math.floor(Math.random() * 30 + 10)}%</div>
                <div className="text-[10px] text-cyan-900">MEM: {Math.floor(Math.random() * 40 + 20)}TB</div>
            </div>
        </div>
    );
};

export default PersonalKernelSidebar;

import React, { useEffect, useState } from 'react';
import { useEngineState } from '../hooks/useEngineState';
import { PHASES } from '../engine/CoreEngine';
import DiagnosisConsole from '../components/console/DiagnosisConsole';
import ProgressMatrix from '../components/dashboard/ProgressMatrix';
import ResultProcessor from '../components/processor/ResultProcessor';
import DynamicGridBackground from '../components/visuals/DynamicGridBackground';
import PersonalKernelSidebar from '../components/sidebar/PersonalKernelSidebar';
import MetadataLog from '../components/sidebar/MetadataLog';

const BootScreen = ({ onStart }) => (
    <div className="flex flex-col items-center justify-center h-full animate-pulse cursor-pointer" onClick={onStart}>
        <h1 className="text-4xl md:text-6xl font-black mb-4 tracking-tighter text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-blue-600">
            FUTURE DISCOVERY
        </h1>
        <p className="text-sm md:text-base text-slate-400 font-mono mb-8">
            [ TOUCH TO INITIALIZE LINK ]
        </p>
        <div className="w-64 h-1 bg-slate-800 rounded-full overflow-hidden">
            <div className="h-full bg-cyan-500 animate-[loading_2s_ease-in-out_infinite] w-1/3"></div>
        </div>
    </div>
);

const ProcessingScreen = () => (
    <div className="flex flex-col items-center justify-center h-full">
        <div className="w-16 h-16 border-4 border-cyan-500 border-t-transparent rounded-full animate-spin mb-4"></div>
        <p className="text-cyan-400 font-mono animate-pulse">CALCULATING TRAJECTORY...</p>
    </div>
);

const MainTerminalLayout = () => {
    const {
        phase,
        currentQuestion,
        currentQuestionIndex,
        weights,
        answers,
        handleAnswer,
        startDiagnosis
    } = useEngineState();

    const [booted, setBooted] = useState(false);

    // Auto-start diagnosis after boot interaction? Or manual.
    const handleBoot = () => {
        setBooted(true);
        startDiagnosis();
    };

    return (
        <div className="flex h-screen w-screen bg-slate-950 text-cyan-400 overflow-hidden relative font-base selection:bg-cyan-900 selection:text-white">
            {/* Background Layer */}
            <div className="absolute inset-0 z-0">
                <DynamicGridBackground weights={weights} />
            </div>

            {/* Left Sidebar */}
            <PersonalKernelSidebar phase={phase} />

            {/* Center Console */}
            <div className="flex-1 flex flex-col p-6 z-10 relative overflow-y-auto">
                <div className="flex-1 flex flex-col justify-center items-center min-h-[600px]">

                    {phase === PHASES.BOOT && <BootScreen onStart={handleBoot} />}

                    {phase === PHASES.DIAGNOSIS && (
                        <div className="w-full flex flex-col items-center transition-all duration-300">
                            <ProgressMatrix
                                currentQuestionIndex={currentQuestionIndex}
                                totalQuestions={25}
                            />
                            <DiagnosisConsole
                                question={currentQuestion}
                                onAnswer={handleAnswer}
                            />
                        </div>
                    )}

                    {phase === PHASES.PROCESSING && <ProcessingScreen />}

                    {phase === PHASES.RESULT && (
                        <ResultProcessor weights={weights} answers={answers} />
                    )}
                </div>

                {/* Footer Status */}
                <div className="absolute bottom-4 left-6 right-6 text-[10px] text-slate-600 flex justify-between font-mono">
                    <span>SYS.VER.0.9.4 // STABLE</span>
                    <span>CONNECTED: NODE_EAST_04</span>
                </div>
            </div>

            {/* Right Sidebar */}
            <MetadataLog weights={weights} />

            {/* Overlay Effects (Noise, Scanlines) */}
            <div className="absolute inset-0 pointer-events-none bg-[url('https://grainy-gradients.vercel.app/noise.svg')] opacity-5 z-50 mix-blend-overlay"></div>
            <div className="absolute inset-0 pointer-events-none bg-gradient-to-b from-transparent via-transparent to-slate-950/80 z-40"></div>

            {/* Heavy Scanline CSS override could be added here or in index.css */}
            <div className="absolute inset-0 pointer-events-none z-50 overflow-hidden opacity-10">
                <div className="w-full h-[2px] bg-white absolute animate-scanline"></div>
            </div>
        </div>
    );
};

export default MainTerminalLayout;
